import React, {useState, useEffect} from "react"
import IntroPage from "./intropage.js"
import Quiz from "./quizpage.js"

function App() {

  const [StartGame, setStartGame] = useState(true)

  const [selected, setSelected] = useState(false)

  const [questions, setQuestions] = useState([])

  const [answerArr, setAnswerArr] = useState([])

  console.log(answerArr)

  useEffect(() => {
    let answerArray = []
    questions.map(q => {
      answerArray.push(q.correct_answer)
      answerArray.push(q.incorrect_answers)
    })
    setAnswerArr(answerArray.flat())
  }, [])

  function answerSelected(){
    setSelected(!selected)
  }

  function beginGame() {
    setStartGame(true)
  }

  useEffect(() => {
    fetch("https://opentdb.com/api.php?amount=5")
      .then(res => res.json())
      .then(data => setQuestions(data.results))
  }, [])

  const displayQuestions = questions.map(q => (


      <Quiz 
      selectAnswer = {answerSelected}
      selected = {selected}
      question = {q.question}
      category = {q.category}
      difficulty = {q.difficulty}
      />


  ))

  return (

    <div className="container">

      {
        StartGame 

        ?
        <>
          {displayQuestions}
          <button className="check-answers-button">Check Answers</button>
        </>



        :

        <IntroPage 
        startGame = {beginGame}

        />
      }

    </div>

  );
}

export default App;

