import React from "react"

export default function Answers(props){

    const styles = props.selected ? "answer-button-selected" : "answer-button-unselected"

    return(
        <div className="answer-button-container">
            <button className={styles} onClick={props.selectAnswer}>Mercury</button>
            <button className={styles} onClick={props.selectAnswer}>Venus</button>
            <button className={styles} onClick={props.selectAnswer}>Mars</button>
            <button className={styles} onClick={props.selectAnswer}>Saturn</button>
        </div>
    )
}


