import React, { useState, useEffect } from "react"
import IntroPage from "./intropage.js"
import Question from "./questions.js"
import Answers from "./answers.js"
import { nanoid } from "nanoid"

function App() {

  const [StartGame, setStartGame] = useState(false)

  const [selected, setSelected] = useState(false)

  const [triviaInfo, setTriviaInfo] = useState([])

  const [answerArr, setAnswerArr] = useState([])

  useEffect(() => {
    fetch("https://opentdb.com/api.php?amount=5&type=multiple")
      .then(res => res.json())
      .then(data => {
        const questions = data.results.map(res => {

          let answers = res.incorrect_answers.concat(res.correct_answer)

          let answersArr = answers.map(a => {
            return({
              answer: a,
              id: {nanoid}
            })
          })

          return({
            question: res.question,
            answers: answers,
            category: res.category,
            difficulty: res.difficulty,
          })
        })
        setTriviaInfo(questions)
      })

  }, [])

  console.log(triviaInfo)

  function answerSelected() {
    setSelected(!selected)
  }

  function beginGame() {
    setStartGame(true)
  }

  const displayTrivia = questions.map(q => {

    let answers = q.incorrect_answers.concat(q.correct_answer)

    return (

      <>
        <Question
          key={q.question}
          question={q.question}
          category={q.category}
          difficulty={q.difficulty}
        />

        <div className="answer-button-container">
          {answers.map(a => {
            return (
              <Answers
                answer={a}
                selectAnswer={answerSelected}
                selected={selected}
                id={nanoid}
              />
            )
          })}
        </div>



        <hr className="divider" />

      </>

    )

  })

  return (

    <div className="container">

      {
        StartGame

          ?
          <>
            {displayTrivia}
            <button className="check-answers-button">Check Answers</button>
          </>

          :

          <IntroPage
            startGame={beginGame}

          />
      }

    </div>

  );
}

export default App;

