import React, { useState, useEffect } from "react"
import IntroPage from "./intropage.js"
import Question from "./questions.js"
import Answers from "./answers.js"
import { nanoid } from "nanoid"

function App() {

  const [StartGame, setStartGame] = useState(false)

  const [questions, setQuestions] = useState([])

  useEffect(() => {
    fetch("https://opentdb.com/api.php?amount=5&type=multiple")
      .then(res => res.json())
      .then(data => {
        const questions = data.results.map(res => {

          let answers = res.incorrect_answers.concat(res.correct_answer)

          const answersArr = answers.map(a =>{
            return({
              answer: a,
              isSelected: false,
              id: {nanoid}
            })
          })

          return({
            question: res.question,
            answers: answersArr,
            category: res.category,
            difficulty: res.difficulty,
            id: {nanoid}
          })
        })
        setQuestions(questions)
      })

  }, [])

  useEffect(function answerSelected(id) {
    setQuestions(oldQuestions => oldQuestions.map(q => {

      const answerArr = q.answers.map(a => {
        return(
          a.id === id ? 
          {...a, isSelected: !a.isSelected} :
          a
        )

      })

      return({
        question: res.question,
        answers: answerArr,
        category: res.category,
        difficulty: res.difficulty,
        id: {nanoid}
      })
    }))
  })

  function beginGame() {
    setStartGame(true)
  }

  const displayTrivia = questions.map(q => {

    return (

      <>
        <Question
          key={q.question}
          question={q.question}
          category={q.category}
          difficulty={q.difficulty}
        />

        <div className="answer-button-container">
          {q.answers.map(a => {
            return (
              <Answers
                answer={a.answer}
                id={a.id}
                selected={a.isSelected}
                selectAnswer={answerSelected}
              />
            )
          })}
        </div>



        <hr className="divider" />

      </>

    )

  })

  return (

    <div className="container">

      {
        StartGame

          ?
          <>
            {displayTrivia}
            <button className="check-answers-button">Check Answers</button>
          </>

          :

          <IntroPage
            startGame={beginGame}

          />
      }

    </div>

  );
}

export default App;

