import React, {useState} from "react"
import {decode} from "html-entities"

export default function Quiz(props){

    const styles = props.selected ? "answer-button-selected" : "answer-button-unselected"

    function sortAnswers(){

        for(let ansArr of props.answerArr){

            for(let a of ansArr){
                return(
                    <button className={styles} onClick={props.selectAnswer}>{a}</button>
                )
            }
        }
    }

    return(
        <div className="quiz-question-container">
            <div className="quiz-question-info-container">
                <p className="quiz-question-info">Category: {props.category}</p>
                <p className="quiz-question-info">Difficulty: {props.difficulty}</p>
            </div>
            
            <p className="quiz-question">{decode(props.question)}</p>
            <div className="answer-button-container">
                <>
                    {sortAnswers}
                </>
                

            </div>
            <hr/>
        </div>
    )
}





