import react from "react"
import {decode} from "html-entities"

export default function FinalAnswers(props){

    const styles = props.selected ? "answer-button-selected" : "answer-button-unselected"
    
    return(
        <>
        <button className={styles} onClick={() => props.selectAnswer(props.id)}>{decode(props.answer)}</button>
        </>
    )
}
