import React, {useState, useEffect} from "react"
import IntroPage from "./intropage.js"
import Quiz from "./quizpage.js"

function App() {

  const [StartGame, setStartGame] = useState(true)

  const [selected, setSelected] = useState(false)

  function answerSelected(){
    setSelected(!selected)
    console.log("yeah")
  }

  function beginGame() {
    setStartGame(true)
  }

  useEffect(() => {
    fetch("https://opentdb.com/api.php?amount=7&category=17&difficulty=medium&type=multiple")
      .then(res => res.json())
      .then(data => console.log(data))
  })

  return (

    <div className="container">

      {StartGame ?

      <Quiz 
      selectAnswer = {answerSelected}
      selected = {selected}

      />

      :

      <IntroPage 
        startGame = {beginGame}

      />}

    </div>

  );
}

export default App;
