import React, {useState, useEffect} from "react"
import IntroPage from "./intropage.js"
import Question from "./questions.js"
import Answers from "./answers.js"

function App() {

  const [StartGame, setStartGame] = useState(false)

  const [selected, setSelected] = useState(false)

  const [questions, setQuestions] = useState([])

  const [answerArr, setAnswerArr] = useState([])

  useEffect(() => {
    let finalAnswerArray = []
    let answerArray =[]
    for(let q of questions){
      answerArray.push(q.correct_answer)
      answerArray.push(q.incorrect_answers)
      finalAnswerArray.push(answerArray.flat())
      answerArray = []
    }
    setAnswerArr(finalAnswerArray)
  }, [])

  function answerSelected(){
    setSelected(!selected)
  }

  function beginGame() {
    setStartGame(true)
  }

  useEffect(() => {
    fetch("https://opentdb.com/api.php?amount=5&type=multiple")
      .then(res => res.json())
      .then(data => setQuestions(data.results))
  }, [])

  const displayTrivia = questions.map(q => {
    return(

      <>
      <Question 
      question = {q.question}
      category = {q.category}
      difficulty = {q.difficulty}
      />

      <Answers 
      answerArr = {answerArr}
      selectAnswer = {answerSelected}
      selected = {selected}
      />

      </>

    )





    })

  return (

    <div className="container">

      {
        StartGame 

        ?
        <>
          {displayTrivia}
          <button className="check-answers-button">Check Answers</button>
        </>



        :

        <IntroPage 
        startGame = {beginGame}

        />
      }

    </div>

  );
}

export default App;

