import React, { useState, useEffect } from "react"
import IntroPage from "./intropage.js"
import Question from "./questions.js"
import Answers from "./answers.js"
import FinalAnswers from "./finalanswers.js"
import { nanoid } from "nanoid"

function App() {

  const [StartGame, setStartGame] = useState(false)

  const [questions, setQuestions] = useState([])

  const [answerCheck, setAnswerCheck] = useState(false)

  useEffect(() => {
    fetch("https://opentdb.com/api.php?amount=5&type=multiple")
      .then(res => res.json())
      .then(data => {
        const questions = data.results.map(res => {

          let answers = res.incorrect_answers.concat(res.correct_answer)

          const answersArr = answers.map(a =>{
            return({
              answer: a,
              isSelected: false,
              isCorrect: a === res.correct_answer ? true : false,
              id: {nanoid}
            })
          })

          return({
            question: res.question,
            answers: shuffle(answersArr),
            category: res.category,
            difficulty: res.difficulty,
            id: {nanoid}
          })
        })
        setQuestions(questions)
      })

  }, [])

  function answerSelected(id) {

    setQuestions(oldQuestions => oldQuestions.map(q => {

      const answerArr = q.answers.map(a => {
        return(
          a.id === id ? 
          {...a, isSelected: !a.isSelected} :
          a
        )

      })

      return({
        question: q.question,
        answers: answerArr,
        category: q.category,
        difficulty: q.difficulty,
        id: {nanoid}
      })

    }))
  }

  function beginGame() {
    setStartGame(true)
  }

  function shuffle(array) {
    let currentIndex = array.length, randomIndex;
    while(currentIndex !=0){
      randomIndex = Math.floor(Math.random() * currentIndex)
      currentIndex--
      [array[currentIndex], array[randomIndex]] = [array[randomIndex], array[currentIndex]]
    }
    return array
  }

  function checkAnswers(){
    setAnswerCheck(true)
  }

  const displayTrivia = questions.map(q => {

    return (

      <>
        <Question
          key={q.question}
          question={q.question}
          category={q.category}
          difficulty={q.difficulty}
        />

        <div className="answer-button-container">
          {q.answers.map(a => {
            return (
              <Answers
                answer={a.answer}
                id={a.id}
                selected={a.isSelected}
                selectAnswer={answerSelected}
              />
            )
          })}
        </div>



        <hr className="divider" />

      </>

    )

  })

  const displayAnswers = questions.map(q => {

    return (

      <>
        <Question
          key={q.question}
          question={q.question}
          category={q.category}
          difficulty={q.difficulty}
        />

        <div className="answer-button-container">
          {q.answers.map(a => {
            return (
              <FinalAnswers
                answer={a.answer}
                id={a.id}
                selected={a.isSelected}
                selectAnswer={answerSelected}
              />
            )
          })}
        </div>



        <hr className="divider" />

      </>

    )

  })

  return (

    <div className="container">

      { () => {
        if(StartGame){
          if(checkAnswers){
            return displayAnswers
          }else{
            return displayTrivia
          }
        }else{
          <IntroPage
            startGame={beginGame}

          />
        }
      }



      }

    </div>

  );
}

export default App;

